import React, { Component } from 'react';
import CompanyImg from '../../assets/images/cred.png';
import experienceIcon from '../../assets/images/experience-icon.svg';
import location from '../../assets/images/location.svg';
import jobType from '../../assets/images/job-type.svg';
// Style
import './styles.scss';

class Cards extends Component {
  render() {
    return (
      <div className='job-cards-style'>
        <img alt='rectangle4' className='company-img' src={CompanyImg} />
        <div className='card-content'>
          <p className='heading'>UI/UX Designer and Illustrator</p>
          <p className='company-name'>Cred</p>
          <div className='icons-text'>
            <img alt='icons' className='icons' src={experienceIcon} />
            <p className='requirement'>Full Time</p>
          </div>
          <div className='icons-text'>
            <img alt='icons' className='icons' src={location} />
            <p className='requirement'>Work from home allowed</p>
          </div>
          <div className='icons-text'>
            <img alt='icons' className='icons' src={jobType} />
            <p className='requirement'>Work experience 1-2 yrs</p>
          </div>
          <button className='btn-container'>Apply Now</button>
        </div>
      </div>
    );
  }
}

export default Cards;
