import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Cards from '../../components/Cards';
import Tabs from '../../components/Tabs';
import Header from '../../components/header';
import Footer from '../../components/footer';
import { withGoogleSheets } from 'react-db-google-sheets';

//Images
import backIcon from '../../assets/images/back-icon.svg';

// Style
import './styles.scss';

const tabsData = ['All', 'New Feature', 'Improvement', 'Fix'];

class Changelogs extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      visibleAll: 2,
      visibleFullTime: 9,
      visibleInternship: 9,
      visibleFreelance: 9,
      error: false,
      tabIndex: 1,
    };

    this.loadMoreAll = this.loadMoreAll.bind(this);
    this.loadMoreFullTime = this.loadMoreFullTime.bind(this);
    this.loadMoreInternship = this.loadMoreInternship.bind(this);
    this.loadMoreFreelance = this.loadMoreFreelance.bind(this);
  }

  changeTab = (val) => {
    this.setState({
      tabIndex: val,
    });
  };

  loadMoreAll() {
    this.setState((prev) => {
      return { visibleAll: prev.visibleAll + 9 };
    });
  }

  loadMoreFullTime() {
    this.setState((prev) => {
      return { visibleFullTime: prev.visibleFullTime + 2 };
    });
  }

  loadMoreInternship() {
    this.setState((prev) => {
      return { visibleInternship: prev.visibleInternship + 2 };
    });
  }

  loadMoreFreelance() {
    this.setState((prev) => {
      return { visibleFreelance: prev.visibleFreelance + 2 };
    });
  }

  render() {
    const { tabIndex } = this.state;
    return (
      <div className='changelogs-page-style'>
        <div className='all-page-style'>
          <div className='header-banner-style'>
            <Header />

            <div className='top-section'>
              <Link to='/' className='back-btn'>
                <img
                  src={backIcon}
                  className='back-btn-icon'
                  alt='back-btn-icon'
                />
                Back to Job Listings
              </Link>
            </div>

            <div className='text-box'>
              <h5 class='heading'>Changelog</h5>
              <p className='para'>
                Last updated :
                {this.props.db.Changelogs.map(
                  (data, index) =>
                    index === this.props.db.Changelogs.length - 1 && (
                      <span className='highlighted-text' key={index}>
                        {data.Date}
                      </span>
                    )
                )}
              </p>

              <Tabs
                tabsData={tabsData}
                tabIndex={tabIndex}
                changeTab={this.changeTab}
              />
            </div>
          </div>
        </div>

        {/* <div className='update-info-container'>
          <p className='update-info'>🎉 Updating New Jobs in 4:00:00 Hrs</p>
        </div> */}
        <div className='job-post-section'>
          <div className='cards-container'>
            <div className='row'>
              {tabIndex === 1 &&
                this.props.db &&
                this.props.db.Changelogs &&
                this.props.db.Changelogs.slice(0, this.state.visibleAll).map(
                  (data, index) => {
                    return (
                      <div className='col-md-4' key={index}>
                        <div className='cards'>
                          <Cards
                            companyImg={data.Logo}
                            position={data.Position}
                            company={data.Company}
                            jobType={data.JobType}
                            location={data.Location}
                            experience={data.Experience}
                            href={data.Link}
                          />
                        </div>
                      </div>
                    );
                  }
                )}
            </div>

            {tabIndex === 1 &&
              this.state.visibleAll < this.props.db.Changelogs.length && (
                <div className='load-more-btn-container'>
                  <button
                    onClick={this.loadMoreAll}
                    type='button'
                    className='load-more'
                  >
                    Load more
                  </button>
                </div>
              )}

            <div className='null-container'>
              {tabIndex === 1 &&
                this.props.db.Changelogs.length === 0 &&
                this.state.visibleFreelance <
                  this.props.db.Changelogs.length && (
                  <p className='null-text'>
                    We will soon update Jobs too, stay tuned.
                  </p>
                )}
            </div>

            <div className='row'>
              {tabIndex === 2 &&
                this.props.db &&
                this.props.db.Changelogs &&
                this.props.db.Changelogs.filter(
                  (data) => data.JobType === 'Full Time'
                )
                  .slice(0, this.state.visibleFullTime)
                  .map((data, index) => {
                    return (
                      <div className='col-md-4' key={index}>
                        <div className='cards'>
                          <Cards
                            companyImg={data.Logo}
                            position={data.Position}
                            company={data.Company}
                            jobType={data.JobType}
                            location={data.Location}
                            experience={data.Experience}
                            href={data.Link}
                          />
                        </div>
                      </div>
                    );
                  })}
            </div>

            {tabIndex === 2 &&
              this.state.visibleFullTime < this.props.db.Changelogs.length && (
                <div className='load-more-btn-container'>
                  <button
                    onClick={this.loadMoreFullTime}
                    type='button'
                    className='load-more'
                  >
                    Load more
                  </button>
                </div>
              )}

            <div className='null-container'>
              {tabIndex === 2 &&
                this.props.db.Changelogs.filter(
                  (data) => data.JobType === 'Full Time'
                ).length === 0 &&
                this.state.visibleFreelance <
                  this.props.db.Changelogs.length && (
                  <p className='null-text'>
                    We will soon update Freelance Jobs too, stay tuned
                  </p>
                )}
            </div>

            <div className='row'>
              {tabIndex === 3 &&
                this.props.db &&
                this.props.db.Changelogs &&
                this.props.db.Changelogs.filter(
                  (data) => data.JobType === 'Internship'
                )
                  .slice(0, this.state.visibleInternship)
                  .map((data, index) => {
                    return (
                      <div className='col-md-4' key={index}>
                        <div className='cards'>
                          <Cards
                            companyImg={data.Logo}
                            position={data.Position}
                            company={data.Company}
                            jobType={data.JobType}
                            location={data.Location}
                            experience={data.Experience}
                            href={data.Link}
                          />
                        </div>
                      </div>
                    );
                  })}
            </div>

            {tabIndex === 3 &&
              this.props.db.Changelogs.filter(
                (data) => data.JobType === 'Internship'
              ).length > 9 &&
              this.state.visibleInternship <
                this.props.db.Changelogs.length && (
                <div className='load-more-btn-container'>
                  <button
                    onClick={this.loadMoreInternship}
                    type='button'
                    className='load-more'
                  >
                    Load more
                  </button>
                </div>
              )}

            <div className='null-container'>
              {tabIndex === 3 &&
                this.props.db.Changelogs.filter(
                  (data) => data.JobType === 'Internship'
                ).length === 0 &&
                this.state.visibleFreelance <
                  this.props.db.Changelogs.length && (
                  <p className='null-text'>
                    We will soon update Freelance Jobs too, stay tuned
                  </p>
                )}
            </div>

            <div className='row'>
              {tabIndex === 4 &&
                this.props.db &&
                this.props.db.Changelogs &&
                this.props.db.Changelogs.filter(
                  (data) => data.JobType === 'Freelance'
                )
                  .slice(0, this.state.visibleFreelance)
                  .map((data, index) => {
                    return (
                      <div className='col-md-4' key={index}>
                        <div className='cards'>
                          <Cards
                            companyImg={data.Logo}
                            position={data.Position}
                            company={data.Company}
                            jobType={data.JobType}
                            location={data.Location}
                            experience={data.Experience}
                            href={data.Link}
                          />
                        </div>
                      </div>
                    );
                  })}
            </div>

            {tabIndex === 4 &&
              this.props.db.Changelogs.filter(
                (data) => data.JobType === 'Freelance'
              ).length > 9 &&
              this.state.visibleFreelance < this.props.db.Changelogs.length && (
                <div className='load-more-btn-container'>
                  <button
                    onClick={this.loadMoreFreelance}
                    type='button'
                    className='load-more'
                  >
                    Load more
                  </button>
                </div>
              )}

            <div className='null-container'>
              {tabIndex === 4 &&
                this.props.db.Changelogs.filter(
                  (data) => data.JobType === 'Freelance'
                ).length === 0 &&
                this.state.visibleFreelance <
                  this.props.db.Changelogs.length && (
                  <p className='null-text'>
                    We will soon update Freelance Jobs too, stay tuned
                  </p>
                )}
            </div>
          </div>
        </div>

        <div className='job-guide-section'>
          <div className='job-guide-container'>
            <p className='job-guide-para text-center top-space'>
              Building an extensive knowledge base to help to make the most out
              of opportunities.{' '}
              <span className='coming-soon'>Coming Soon 😉</span>
            </p>

            <Footer />
          </div>
        </div>
      </div>
    );
  }
}

export default withGoogleSheets('Changelogs')(Changelogs);
