import React, { useState, useEffect } from 'react';
import Cards from '../../components/Cards';
import Tabs from '../../components/Tabs';
import Header from '../../components/header';
import Footer from '../../components/footer';
import JobGuideCard from '../../components/JobGuideCard';
import { withGoogleSheets } from 'react-db-google-sheets';
import PropTypes from 'prop-types';

// Style
import './styles.scss';

const tabsData = ['All', 'Full Time', 'Internship', 'Freelance'];

const jobsPerPage = 9;
const arrayForHoldingJobs = [];

const [jobsToShow, setjobsToShow] = useState([]);
const [count, setCount] = useState(1);

const loopThroughJobs = (count) => {
  for (
    let i = count * jobsPerPage - jobsPerPage;
    i < jobsPerPage * count;
    i++
  ) {
    if (jobs[i] !== undefined) {
      arrayForHoldingJobs.push(jobs[i]);
    }
  }
  setJobsToShow(arrayForHoldingJobs);
};

useEffect(() => {
  setCount((prevCount) => prevCount + 1);
  loopThroughJobs(count);
}, []);
const handleShowMoreJobs = () => {
  setCount((prevCount) => prevCount + 1);
  loopThroughJobs(count);
};

const Home = (props) => {
  const [tabIndex, setValue] = useState(1);

  const changeTab = (val) => {
    const tabIndex = val;
    setValue(tabIndex);
  };

  return (
    <div className='home-page-style'>
      <div class='all-page-style'>
        <div class='header-banner-style'>
          <Header />

          <div class='text-box'>
            <p className='status-card'>Early Access</p>
            <h5 class='heading'>Your destination for handpicked Design Jobs</h5>

            <p class='para'>
              Register to get exclusive information and job letter
            </p>
          </div>
          <div className='register-box'>
            <input
              type='text'
              placeholder='Enter your Email address'
              className='register-input'
            />
            <button className='register-btn'>
              Register Now
              <br />
              <span className='btn-span-text'>and join 800+ Designers</span>
            </button>
          </div>
        </div>
      </div>

      {/* <div className='update-info-container'>
          <p className='update-info'>ðŸŽ‰ Updating New Jobs in 4:00:00 Hrs</p>
        </div> */}
      <div class='job-post-section'>
        <div className='cards-container'>
          <div className='cards-top-section'>
            <div className='text-box'>
              <h5 className='post-heading'>Job Posts</h5>
              <p className='post-info-para'>
                <span className='highlighted-text'>
                  {props.db.Sheet1.length}+ Design Jobs
                </span>{' '}
                are available to apply for right now.
              </p>
              <p className='post-info-para'>
                Last updated on{' '}
                {props.db.Sheet1.map(
                  (data, index) =>
                    index === props.db.Sheet1.length - 1 && (
                      <span className='highlighted-text' key={index}>
                        {data.Timestamp}
                      </span>
                    )
                )}
              </p>
            </div>

            <Tabs
              tabsData={tabsData}
              tabIndex={tabIndex}
              changeTab={changeTab}
            />
          </div>

          <div className='row'>
            {tabIndex === 1 &&
              props.db &&
              props.db.Sheet1 &&
              props.db.Sheet1.map((data, index) => {
                return (
                  <div className='col-md-4'>
                    <div className='cards'>
                      <Cards
                        companyImg={data.Logo}
                        position={data.Position}
                        company={data.Company}
                        jobType={data.JobType}
                        location={data.Location}
                        experience={data.Experience}
                        href={data.Link}
                      />
                    </div>
                  </div>
                );
              })}

            {tabIndex === 2 &&
              props.db &&
              props.db.Sheet1 &&
              props.db.Sheet1.filter(
                (data) => data.JobType === 'Full Time'
              ).map((data) => {
                return (
                  <div className='col-md-4'>
                    <div className='cards'>
                      <Cards
                        companyImg={data.Logo}
                        position={data.Position}
                        company={data.Company}
                        jobType={data.JobType}
                        location={data.Location}
                        experience={data.Experience}
                        href={data.Link}
                      />
                    </div>
                  </div>
                );
              })}

            {tabIndex === 3 &&
              props.db &&
              props.db.Sheet1 &&
              props.db.Sheet1.filter(
                (data) => data.JobType === 'Internship'
              ).map((data) => {
                return (
                  <div className='col-md-4'>
                    <div className='cards'>
                      <Cards
                        companyImg={data.Logo}
                        position={data.Position}
                        company={data.Company}
                        jobType={data.JobType}
                        location={data.Location}
                        experience={data.Experience}
                        href={data.Link}
                      />
                    </div>
                  </div>
                );
              })}

            {tabIndex === 4 &&
              props.db &&
              props.db.Sheet1 &&
              props.db.Sheet1.filter(
                (data) => data.JobType === 'Freelance'
              ).map((data) => {
                return (
                  <div className='col-md-4'>
                    <div className='cards'>
                      <Cards
                        companyImg={data.Logo}
                        position={data.Position}
                        company={data.Company}
                        jobType={data.JobType}
                        location={data.Location}
                        experience={data.Experience}
                        href={data.Link}
                      />
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      </div>

      <button onClick={handleShowMoreJobs}>Load more</button>

      <div className='job-guide-section'>
        <div className='job-guide-container'>
          <div className='text-box'>
            <h5 className='post-heading'>Job Guide</h5>
            <p className='post-info-para'>
              A few resources to help you ace your next opportunity
            </p>
          </div>

          <div className='row'>
            {props.db &&
              props.db.Guide &&
              props.db.Guide.map((data, index) => {
                return (
                  <div className='col-md-4'>
                    <div className='top-space'>
                      <JobGuideCard
                        title={data.Title}
                        articleType='Job Application'
                        readingTime={data.Time}
                        href=''
                        cardImg={data.Image}
                      />
                    </div>
                  </div>
                );
              })}
          </div>

          <p className='job-guide-para text-center top-space'>
            Building an extensive knowledge base to help to make the most out of
            opportunities. <span className='coming-soon'>Coming Soon ðŸ˜‰</span>
          </p>

          <Footer />
        </div>
      </div>
    </div>
  );
};

Home.propTypes = {
  db: PropTypes.shape({
    Sheet1: PropTypes.arrayOf(PropTypes.object),
  }),
};

export default withGoogleSheets(['Sheet1', 'Guide'])(Home);
