import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Cards from '../../components/Cards';
import Tabs from '../../components/Tabs';
import Header from '../../components/header';
import Footer from '../../components/footer';
import { withGoogleSheets } from 'react-db-google-sheets';

//Images
import backIcon from '../../assets/images/back-icon.svg';

// Style
import './styles.scss';
import ChangelogCards from '../../components/ChangelogCard';

const tabsData = ['All', 'New Feature', 'Improvement', 'Fix'];

class Changelogs extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      visibleAll: 2,
      visibleNewFeature: 2,
      visibleInprovement: 2,
      visibleFix: 2,
      error: false,
      tabIndex: 1,
    };

    this.loadMoreAll = this.loadMoreAll.bind(this);
    this.loadMoreNewFeature = this.loadMoreNewFeature.bind(this);
    this.loadMoreInprovement = this.loadMoreInprovement.bind(this);
    this.loadMoreFix = this.loadMoreFix.bind(this);
  }

  changeTab = (val) => {
    this.setState({
      tabIndex: val,
    });
  };

  loadMoreAll() {
    this.setState((prev) => {
      return { visibleAll: prev.visibleAll + 9 };
    });
  }

  loadMoreNewFeature() {
    this.setState((prev) => {
      return { visibleNewFeature: prev.visibleNewFeature + 2 };
    });
  }

  loadMoreInprovement() {
    this.setState((prev) => {
      return { visibleInprovement: prev.visibleInprovement + 2 };
    });
  }

  loadMoreFix() {
    this.setState((prev) => {
      return { visibleFix: prev.visibleFix + 2 };
    });
  }

  render() {
    const { tabIndex } = this.state;
    return (
      <div className='changelogs-page-style'>
        <div className='all-page-style'>
          <div className='header-banner-style'>
            <Header />

            <div className='top-section'>
              <Link to='/' className='back-btn'>
                <img
                  src={backIcon}
                  className='back-btn-icon'
                  alt='back-btn-icon'
                />
                Back to Job Listings
              </Link>
            </div>

            <div className='text-tab-section'>
              <div className='text-box'>
                <h5 class='heading'>Changelog</h5>
                <p className='para'>
                  Last updated :
                  {this.props.db.Changelogs.map(
                    (data, index) =>
                      index === this.props.db.Changelogs.length - 1 && (
                        <span className='highlighted-text' key={index}>
                          {data.Date}
                        </span>
                      )
                  )}
                </p>
              </div>
              <Tabs
                tabsData={tabsData}
                tabIndex={tabIndex}
                changeTab={this.changeTab}
              />
            </div>
          </div>

          <div className='change-logs-section'>
            <div className='cards-container'>
              <div className='row'>
                {tabIndex === 1 &&
                  this.props.db &&
                  this.props.db.Changelogs &&
                  this.props.db.Changelogs.slice(0, this.state.visibleAll).map(
                    (data, index) => {
                      return (
                        <div className='col-md-7' key={index}>
                          <div className='cards'>
                            <ChangelogCards
                              title={data.Title}
                              description={data.Description}
                              name={data.Updater}
                              position={data.Position}
                              personImg={data.Image}
                              status={data.Type}
                              time={data.Date}
                            />
                          </div>
                        </div>
                      );
                    }
                  )}
              </div>

              {tabIndex === 1 &&
                this.state.visibleAll < this.props.db.Changelogs.length && (
                  <div className='load-more-btn-container'>
                    <button
                      onClick={this.loadMoreAll}
                      type='button'
                      className='load-more'
                    >
                      Load more
                    </button>
                  </div>
                )}

              <div className='null-container'>
                {tabIndex === 1 &&
                  this.props.db.Changelogs.length === 0 &&
                  this.state.visibleFix < this.props.db.Changelogs.length && (
                    <p className='null-text'>
                      We will soon update Jobs too, stay tuned.
                    </p>
                  )}
              </div>

              <div className='row'>
                {tabIndex === 2 &&
                  this.props.db &&
                  this.props.db.Changelogs &&
                  this.props.db.Changelogs.filter(
                    (data) => data.JobType === 'Full Time'
                  )
                    .slice(0, this.state.visibleNewFeature)
                    .map((data, index) => {
                      return (
                        <div className='col-md-4' key={index}>
                          <div className='cards'>
                            <Cards
                              companyImg={data.Logo}
                              position={data.Position}
                              company={data.Company}
                              jobType={data.JobType}
                              location={data.Location}
                              experience={data.Experience}
                              href={data.Link}
                            />
                          </div>
                        </div>
                      );
                    })}
              </div>

              {tabIndex === 2 &&
                this.state.visibleNewFeature <
                  this.props.db.Changelogs.length && (
                  <div className='load-more-btn-container'>
                    <button
                      onClick={this.loadMoreNewFeature}
                      type='button'
                      className='load-more'
                    >
                      Load more
                    </button>
                  </div>
                )}

              <div className='null-container'>
                {tabIndex === 2 &&
                  this.props.db.Changelogs.filter(
                    (data) => data.JobType === 'Full Time'
                  ).length === 0 &&
                  this.state.visibleFix < this.props.db.Changelogs.length && (
                    <p className='null-text'>
                      We will soon update Fix Jobs too, stay tuned
                    </p>
                  )}
              </div>

              <div className='row'>
                {tabIndex === 3 &&
                  this.props.db &&
                  this.props.db.Changelogs &&
                  this.props.db.Changelogs.filter(
                    (data) => data.JobType === 'Inprovement'
                  )
                    .slice(0, this.state.visibleInprovement)
                    .map((data, index) => {
                      return (
                        <div className='col-md-4' key={index}>
                          <div className='cards'>
                            <Cards
                              companyImg={data.Logo}
                              position={data.Position}
                              company={data.Company}
                              jobType={data.JobType}
                              location={data.Location}
                              experience={data.Experience}
                              href={data.Link}
                            />
                          </div>
                        </div>
                      );
                    })}
              </div>

              {tabIndex === 3 &&
                this.props.db.Changelogs.filter(
                  (data) => data.JobType === 'Inprovement'
                ).length > 9 &&
                this.state.visibleInprovement <
                  this.props.db.Changelogs.length && (
                  <div className='load-more-btn-container'>
                    <button
                      onClick={this.loadMoreInprovement}
                      type='button'
                      className='load-more'
                    >
                      Load more
                    </button>
                  </div>
                )}

              <div className='null-container'>
                {tabIndex === 3 &&
                  this.props.db.Changelogs.filter(
                    (data) => data.JobType === 'Inprovement'
                  ).length === 0 &&
                  this.state.visibleFix < this.props.db.Changelogs.length && (
                    <p className='null-text'>
                      We will soon update Fix Jobs too, stay tuned
                    </p>
                  )}
              </div>

              <div className='row'>
                {tabIndex === 4 &&
                  this.props.db &&
                  this.props.db.Changelogs &&
                  this.props.db.Changelogs.filter(
                    (data) => data.JobType === 'Fix'
                  )
                    .slice(0, this.state.visibleFix)
                    .map((data, index) => {
                      return (
                        <div className='col-md-4' key={index}>
                          <div className='cards'>
                            <Cards
                              companyImg={data.Logo}
                              position={data.Position}
                              company={data.Company}
                              jobType={data.JobType}
                              location={data.Location}
                              experience={data.Experience}
                              href={data.Link}
                            />
                          </div>
                        </div>
                      );
                    })}
              </div>

              {tabIndex === 4 &&
                this.props.db.Changelogs.filter(
                  (data) => data.JobType === 'Fix'
                ).length > 9 &&
                this.state.visibleFix < this.props.db.Changelogs.length && (
                  <div className='load-more-btn-container'>
                    <button
                      onClick={this.loadMoreFix}
                      type='button'
                      className='load-more'
                    >
                      Load more
                    </button>
                  </div>
                )}

              <div className='null-container'>
                {tabIndex === 4 &&
                  this.props.db.Changelogs.filter(
                    (data) => data.JobType === 'Fix'
                  ).length === 0 &&
                  this.state.visibleFix < this.props.db.Changelogs.length && (
                    <p className='null-text'>
                      We will soon update Fix Jobs too, stay tuned
                    </p>
                  )}
              </div>
            </div>
          </div>

