import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import Header from '../../components/header';
import Footer from '../../components/footer';
import JobGuideCard from '../../components/JobGuideCard';
import { withGoogleSheets } from 'react-db-google-sheets';

//Images
import backIcon from '../../assets/images/back-icon.svg';

// Style
import './styles.scss';

const SPREADSHEET_ID = '1cdt5li5fmYpP1DBAU9KNKMrN3s1AoonNrttd7BjYhsE';

const options = {
  margin: 30,
  nav: true,
  loop: true,
  dots: false,
  autoplay: false,
  navText: [
    "<div class='nav-btn prev-slide'></div>",
    "<div class='nav-btn next-slide'></div>",
  ],
  smartSpeed: 1000,
  responsive: {
    0: {
      items: 1,
    },
    400: {
      items: 1,
    },
    600: {
      items: 2,
    },
    700: {
      items: 3,
    },
    1000: {
      items: 3,
    },
  },
};

const screenWidth = window.innerWidth;

class JobGuide extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isMobile: false,
    };

    this.onFormSubmit = this.onFormSubmit.bind(this);
  }

  componentDidMount() {
    this.checkViewportType();

    window.addEventListener('resize', this.resize.bind(this));
    this.resize();
  }

  resize = () => {
    this.setState({ isMobile: window.innerWidth < 768 });
  };

  checkViewportType = () => {
    this.setState({
      isMobile: screenWidth > 768 ? false : true,
    });
  };

  onFormSubmit(submissionValues) {
    const params = {
      spreadsheetId: SPREADSHEET_ID,
      // The A1 notation of a range to search for a logical table of data.Values will be appended after the last row of the table.
      range: 'Guide',
      valueInputOption: '1', //RAW = if no conversion or formatting of submitted data is needed. Otherwise USER_ENTERED
      // How the input data should be inserted.
      insertDataOption: 'OVERWRITE', //Choose OVERWRITE OR INSERT_ROWS
    };

    const valueRangeBody = {
      majorDimension: 'ROWS', //log each entry as a new row (vs column)
      values: [], //convert the object's values to an array
    };

    let request = this.props.db.Guide.value.append(params, valueRangeBody);
    request.then(
      function (response) {
        // TODO: Insert desired response behaviour on submission
        console.log(response.result);
      },
      function (reason) {
        console.error('error: ' + reason.result.error.message);
      }
    );
  }

  render() {
    const selectedJobId = this.props.match.params.id;
    return (
      <div className='job-guide-page-style' ref={this.myRef}>
        <div class='all-page-style'>
          <div class='header-banner-style'>
            <Header />

            <div className='top-section'>
              <Link to='/' className='back-btn'>
                <img
                  src={backIcon}
                  className='back-btn-icon'
                  alt='back-btn-icon'
                />
                Back to Job Listings
              </Link>
            </div>

            <button type='submit' onClick={this.onFormSubmit}>
              Submit
            </button>

            {this.props.db &&
              this.props.db.Guide &&
              this.props.db.Guide.map(
                (data, index) =>
                  index === parseInt(selectedJobId) - 1 && (
                    <div key={index}>
                      <div class='text-box'>
                        <h5 class='heading'>{data.Title}</h5>
                        <p class='para'>{data.Time}</p>
                      </div>

                      <div className='purpose-box-container'>
                        <h5 class='heading purpose-heading'>ðŸŽ¯ Purpose</h5>
                        <p class='para purpose-para'>{data.Purpose}</p>
                      </div>

                      <div className='row'>
                        <div className='col-md-6'>
                          <div className='methods'>
                            <h5 class='heading purpose-heading'>Doâ€™s</h5>
                            <pre className='para purpose-para do-para'>
                              {/* {data.Do.split('\n').map((para, index) => para)} */}
                              {data.Do}
                            </pre>
                          </div>
                        </div>

                        <div className='col-md-6'>
                          <div className='methods'>
                            <h5 class='heading purpose-heading'>Dontâ€™s</h5>
                            <pre className='para purpose-para do-para'>
                              {data.DoNots}
                            </pre>
                          </div>
                        </div>
                      </div>
                    </div>
                  )
              )}
          </div>
        </div>

        {/* <div className='update-info-container'>
          <p className='update-info'>ðŸŽ‰ Updating New Jobs in 4:00:00 Hrs</p>
        </div> */}

        <div className='job-guide-section'>
          <div className='job-guide-container'>
            <div className='text-box'>
              <h5 className='post-heading'>Other helpful guides</h5>
              <p className='post-info-para'>
                A few resources to help you ace your next opportunity
              </p>
            </div>

            <div className='row'>
              <OwlCarousel className='owl-theme' {...options}>
                {this.props.db &&
                  this.props.db.Guide &&
                  this.props.db.Guide.map(
                    (data, index) =>
                      index !== parseInt(selectedJobId) - 1 && (
                        <div className='item' key={index}>
                          <div className='top-space'>
                            <JobGuideCard
                              title={data.Title}
                              articleType='Job Application'
                              readingTime={data.Time}
                              selectedArticleId={data.ID}
                              cardImg={data.Image}
                            />
                          </div>
                        </div>
                      )
                  )}
              </OwlCarousel>
            </div>

            <p className='job-guide-para text-center top-space'>
              Building an extensive knowledge base to help to make the most out
              of opportunities.{' '}
              <span className='coming-soon'>Coming Soon ðŸ˜‰</span>
            </p>

            <Footer />
          </div>
        </div>
      </div>
    );
  }
}

export default withGoogleSheets('Guide')(JobGuide);
