import React, { Component } from 'react';
import { BrowserRouter, Switch, Route } from 'react-router-dom';

// Personal Information
import PersonalInformation from './containers/basicDetails/PersonalInformation';
import SelectCity from './containers/basicDetails/SelectCity';
import SetLocation from './containers/basicDetails/SetLocation';
import SelectLanguage from './containers/basicDetails/SelectLanguage';
import SetLocationMap from './containers/basicDetails/SetLocationMap';
import PersonalInfoDetails from './containers/basicDetails/PersonalInfoDetails';

// Personal Documents
import PersonalDocuments from './containers/personalDocument/PersonalDocuments';
import PanCard from './containers/personalDocument/PanCard';
import PanCardDetails from './containers/personalDocument/PanCardDetails';
import DrivingLicense from './containers/personalDocument/DrivingLicense';
import DrivingLicenseDetails from './containers/personalDocument/DrivingLicenseDetails';
import AadharCard from './containers/personalDocument/AadharCard';
import AadharCardDetails from './containers/personalDocument/AadharCardDetails';

/* Bike Related Documents */
import VehicleDetails from './containers/bikeRelatedDocuments/VehicleDetails';
import VehicleInput from './containers/bikeRelatedDocuments/VehicleInput';

/* Bank Account Details */
import IfscCode from './containers/bankAccountDetails/IfscCode';
import AccountDetails from './containers/bankAccountDetails/AccountDetails';
import AccountInput from './containers/bankAccountDetails/AccountInput';

/* Contact Information */
import ContactDetails from './containers/contactInformation/ContactDetails';
import ContactInput from './containers/contactInformation/ContactInput';

/* Work Preference */
import OrderType from './containers/workPreference/OrderType';
import PreferredArea from './containers/workPreference/PreferredArea';
import PreferenceDetail from './containers/workPreference/PreferenceDetail';
import WorkLocation from './containers/workPreference/WorkLocation';
// import VehicleDetailsInput from './containers/bikeRelatedDocuments/VehicleDetailsInput';

import Dashboard from './containers/Dashboard';
import RouteConstants from './common/routeConstants';

class AppRouter extends Component {
  render() {
    return (
      <BrowserRouter>
        <div className='desktop-mode-disabled'>
          <h1 className='heading-large'>
            Sorry! This app is incompatible with Desktop view. Please switch to
            mobile view to use this app.
          </h1>
        </div>

        <Switch>
          <Route exact path='/' component={Dashboard} />

          {/* Personal Information */}
          <Route
            exact
            path={RouteConstants.personalInformation}
            component={PersonalInformation}
          />
          <Route
            exact
            path={RouteConstants.personalInformationDetails}
            component={PersonalInfoDetails}
          />
          <Route
            exact
            path={RouteConstants.personalInformationCity}
            component={SelectCity}
          />
          <Route
            exact
            path={RouteConstants.personalInformationLocation}
            component={SetLocation}
          />
          <Route
            exact
            path={RouteConstants.personalInformationLocationMap}
            component={SetLocationMap}
          />
          <Route
            exact
            path={RouteConstants.personalInformationLanguage}
            component={SelectLanguage}
          />

          {/* Personal Documents */}
          <Route
            exact
            path={RouteConstants.personalDocuments}
            component={PersonalDocuments}
          />
          <Route
            exact
            path={RouteConstants.personalDocumentsPAN}
            component={PanCard}
          />
          <Route
            exact
            path={RouteConstants.personalDocumentsPANDetails}
            component={PanCardDetails}
          />
          <Route
            exact
            path={RouteConstants.personalDocumentsDL}
            component={DrivingLicense}
          />
          <Route
            exact
            path={RouteConstants.personalDocumentsDLDetails}
            component={DrivingLicenseDetails}
          />
          <Route
            exact
            path={RouteConstants.personalDocumentsAadhar}
            component={AadharCard}
          />
          <Route
            exact
            path={RouteConstants.personalDocumentsAadharDetails}
            component={AadharCardDetails}
          />

          {/* Bike Related Document */}
          <Route
            exact
            path={RouteConstants.vehicleDetailsDisplay}
            component={VehicleDetails}
          />
          <Route
            exact
            path={RouteConstants.vehicleDetails}
            component={VehicleInput}
          />

          {/* Bank Account Details */}
          <Route
            exact
            path={RouteConstants.bankDetailsIfsc}
            component={IfscCode}
          />
          <Route
            exact
            path={RouteConstants.bankDetails}
            component={AccountDetails}
          />
          <Route
            exact
            path={RouteConstants.bankDetailsAccount}
            component={AccountInput}
          />

          {/* Contact Information */}
          <Route
            exact
            path={RouteConstants.emergencyContactDetails}
            component={ContactDetails}
          />
          <Route
            exact
            path={RouteConstants.emergencyContact}
            component={ContactInput}
          />

          {/* Work Preference */}
          <Route exact path={RouteConstants.orderType} component={OrderType} />
          <Route
            exact
            path={RouteConstants.workLocation}
            component={WorkLocation}
          />
          <Route
            exact
            path={RouteConstants.workArea}
            component={PreferredArea}
          />
          <Route
            exact
            path={RouteConstants.workDetails}
            component={PreferenceDetail}
          />
        </Switch>
      </BrowserRouter>
    );
  }
}

export default AppRouter;
