import React, { Component } from 'react';
import Cards from '../../components/Cards';
import Tabs from '../../components/Tabs';
import Header from '../../components/header';
import { withGoogleSheets } from 'react-db-google-sheets';
import PropTypes from 'prop-types';

// Style
import './styles.scss';

const tabsData = ['All', 'Full Time', 'Internship', 'Freelance'];

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tabIndex: 1,
    };
  }

  changeTab = (val) => {
    this.setState({
      tabIndex: val,
    });
  };

  render() {
    const { tabIndex } = this.state;
    return (
      <div className='home-page-style'>
        <div class='header-banner-style'>
          <Header />

          <div class='text-box'>
            <h5 class='heading'>Your destination for handpicked Design Jobs</h5>
            <p class='para'>
              Register to get exclusive information and job letter
            </p>
          </div>
          <div className='register-box'>
            <input
              type='text'
              placeholder='Enter your Email address'
              className='register-input'
            />
            <button className='register-btn'>
              Register Now
              <br />
              <span className='btn-span-text'>and join 800+ Designers</span>
            </button>
          </div>
        </div>

        <div className='update-info-container'>
          <p className='update-info'>🎉 Updating New Jobs in 4:00:00 Hrs</p>
        </div>

        <div className='cards-container'>
          <div className='cards-top-section'>
            <div className='text-box'>
              <h5 className='post-heading'>Job Posts</h5>
              <p className='post-info-para'>
                <span className='highlighted-text'>370+ Design Jobs</span> are
                available to apply for right now.
              </p>
              <p className='post-info-para'>
                Last updated on{' '}
                <span className='highlighted-text'>
                  Sunday, 03 october at 12:05 AM
                </span>
              </p>
            </div>

            <Tabs
              tabsData={tabsData}
              tabIndex={tabIndex}
              changeTab={this.changeTab}
            />
          </div>

          <div className='row'>
            {tabIndex === 1 &&
              this.props.db &&
              this.props.db.Sheet1 &&
              this.props.db.Sheet1.map((data) => {
                return (
                  <div className='col-md-4'>
                    <div className='cards'>
                      <Cards
                        companyImg={data.Logo}
                        position={data.Position}
                        company={data.Company}
                        jobType={data.JobType}
                        location={data.Location}
                        experience={data.Experience}
                        href={data.LinkToApply}
                      />
                    </div>
                  </div>
                );
              })}

            {tabIndex === 2 &&
              this.props.db &&
              this.props.db.Sheet1 &&
              this.props.db.Sheet1.map((data) => {
                return (
                  <div className='col-md-4'>
                    <span>{data.JobType}</span>
                    <div className='cards'>
                      <Cards
                        companyImg={data.Logo}
                        position={data.Position}
                        company={data.Company}
                        jobType={data.JobType}
                        location={data.Location}
                        experience={data.Experience}
                        href={data.LinkToApply}
                      />
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    );
  }
}

Home.propTypes = {
  db: PropTypes.shape({
    Sheet1: PropTypes.arrayOf(PropTypes.object),
  }),
};

export default withGoogleSheets('Sheet1')(Home);
